import React, { useEffect } from 'react';
import { useState } from "react";
import { common, msgStyle } from "../common/common";
import $ from 'jquery'; 
import { json } from 'stream/consumers';

const Footer = () => {

  const lng = common.lng;
  const sid = common.getCookie('sid');	
  const logedId = common.getCookie('logedId');	
  const logedNick = common.getCookie('logedNick');	
  const logedAvatar = common.getCookie('logedAvatar');	

  // onLoad event handler
   useEffect(() => {
    $('#waiting').hide();
  });	


    return (
    <div id="Footer">
        <div className="row">
            <section className="mb-4">
            <span>{ lng('SHARE') }</span>&nbsp;	
            <a className="btn btn-outline-light btn-floating m-1" title="Facebbok megosztás" id="shareFb"
                href="'https://www.facebook.com/sharer/sharer.php?u='+location"
                role="button" target="_new"><em className="fas fa-facebook-f"></em></a>

            <a className="btn btn-outline-light btn-floating m-1" title="Twitter megosztás" id="shareTw"
                href="'https://twitter.com/intent/tweet?&url='+location"
                role="button" target="_new"><i className="fas fa-twitter"></i></a>

            <a className="btn btn-outline-light btn-floating m-1" title="url küldése gmail -en" id="shareMail"
                href="'https://mail.google.com/mail/?view=cm&body='+location"
                role="button" target="_new"><i className="fas fa-envelope"></i></a>

            <a className="btn btn-outline-light btn-floating m-1" id="shareIn"
                href="'https://www.instagram.com/sharer.php?u='+location"
                role="button" target="_new"><i className="fas fa-instagram"></i></a>

            <a className="btn btn-outline-light btn-floating m-1"  id="shareIn"
                href="https://www.instagram.com/?url=https://www.drdrop.co/" target="_blank" rel="noopener">
                <i className="fas fa-instagram"></i>
            </a>

            <a className="btn btn-outline-light btn-floating m-1" title="Linkedin megsztás"  id="shareLi"
                href="'https://www.linkedin.com/cws/share?url='+location"
                role="button" target="_new"><i className="fab fa-linkedin-in"></i
            ></a>
            </section>
        </div>

        <div className="row">
            <div className="col-12">	
                <a href="HREF('home.impressum',{})">Imresszum</a>&nbsp;&nbsp;&nbsp;
                <a href="HREF('home.policy',{})">{ lng('POLICY') }</a>&nbsp;&nbsp;&nbsp;
                <a href="HREF('home.protest',{})">{ lng('PROTEST') }</a>&nbsp;&nbsp;&nbsp;
                <a href="HREF('home.licence',{})">{ lng('LICENCE') }</a>&nbsp;&nbsp;&nbsp;
                <a href="https://github.com/utopszkij/utopszkij_fw" target="_new">
                    { lng('SOURCE') }</a>&nbsp;&nbsp;&nbsp;
            </div>	
            <div className="col-12">	
                Ⓒ 2024 { lng('COPYRIGHT') }
            </div>	
            <div className="col-12">	
                <a href="HREF('home.sponzor',{})">{ lng('SPONZOR') }</a>&nbsp;&nbsp;&nbsp;
            </div>	
        </div>

    </div>
   );     

};

export default Footer;
