import React, { useEffect } from 'react';
import { useState } from "react";
import { common, msgStyle  } from "../common/common";
import $ from 'jquery'; 
import { json } from 'stream/consumers';

const Login = () => {

  //variables whose value can change during runtime
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [task, setTask] = useState("init");
  const lng = common.lng;
  const sid = common.data('sid');	

  // onLoad event handler
	   useEffect(() => {
		$('#waiting').hide();
	});	


  /** validate import fields
	* if OK submit form
	* else show errorMsg, set error class, set focus
	* @return void 
  */	
  const doLogin = () => {

	console.log(document); 
	console.log(document.isConnected); 
	console.log(window.Navigator.name);

	if (email === '') {
		common.popupMsg(lng('NICK_OR_EMAIL_REQUIRED'), msgStyle.error);
	} else if (password === '') {
		common.popupMsg(lng('PASSWORD_REQUIRED'), msgStyle.error);
	} else {
		common.callApi('api/dologin.php',{
			'sid': sid,
			'email': email,
			'password':password})
		.then( (res:any) => { 
			if (res.data.loged) {
				common.setCookie('logedId',res.data.id,10);
				common.setCookie('logedNick',res.data.nick,10);
				common.setCookie('logedAvatar',res.data.avatar,10);
				common.setCookie('logedGroups',res.data.groups,10);
				console.log(document.location.href);

				// navigation not implelnted in jest test
				if (document.location.href.indexOf('/login') > 0) {
					// not in jest test
					document.location.href = './home';
				} else {
					// in jest test
					common.setCookie('href','./home',1);
				}	

			} else {
				common.popupMsg(lng('WRONG_DATA'), msgStyle.error);
			}	
		});	
	}
  };

  const registClick = () => {

  }

  const forgetPasswordClick = () => {

  }

  const resendEmailClick = () => {

  }

  return (
		<div id="login" className="form">
			{(task === 'regist') &&
				( <p>Regist_click</p>)
			}
			<div className="row text-center">
				<h1>
						<em className="fas fa-sign-in-alt"></em>&nbsp;
						<span>{ lng('LOGIN') }</span>
				</h1>
			</div>	
			<form name="login" action="index.php" method="post" 
				id="formLogin">
				<div className="row text-center">
					<div className="col-12">
						<em className="fas fa-user"></em>&nbsp;
						<label>{ lng('NICKNAME_OR_EMAIL') }</label><br />
						<input type="text" name="email" id="email" value={ email }
							onChange={(e) => setEmail(e.target.value)} autoFocus
							required />
					</div>	
				</div>	
				<div className="row text-center">
					<div className="col-12">
						<em className="fas fa-key"></em>&nbsp;
						<label>{ lng('PASSWORD') }</label><br />
						<input type="password" id="password" name="password" value={ password }
								onChange={(e) => setPassword(e.target.value)}
								required />
					</div>
				</div>	
				<div className="row text-center">
					<div className="col-12">
						<a href="valami" target="_new">{ lng('ACCEPT1') }</a>&nbsp;
						{ lng('ACCEPT2')}
					</div>
				</div>	

				<div className="row text-center">
					<div className="col-12">
						<button type="button" className="btn btn-success" name="submit" id="doLogin" onClick={ doLogin }>
							<em className="fas fa-check"></em>&nbsp;{ lng('OK') }
						</button>   
					</div>
				</div>	
				<br /><br />
				<div className="row text-center">
				<div className="col-12">
						<a href="'https://netpolgar.hu/auth/facebook?state='+SITEURL+'/'+atob(redirect)"
							className="link"> 
							{ lng('FBLOGIN') }			
						</a>
					</div>
					<div className="col-12">
						<a href="'https://netpolgar.hu/auth/google?state='+SITEURL+'/'+atob(redirect)" 
							className="link"> 
							{ lng('GOOGLELOGIN') }			
						</a>
					</div>	
				</div>	
			</form>
			<div className="alert alert-info">{ lng('REGISTHELP2') }</div>
			<div className="row">
				<div className="col-12 text-center">
					<button type="button" id="doRegister" onClick={ registClick } className="link">
							{ lng('DOREGIST') }
					</button>
				</div>
			</div>
			<div className="row">
				<div className="col-12 text-center">
					<var className="link" id="resendEmail" onClick={ resendEmailClick }>
							{ lng('RESEND_ACTIVATOR') }
					</var>
				</div>
			</div>
			<div className="row">
				<div className="col-12 text-center">
					<var className="link" id="forgetPassword" onClick={ forgetPasswordClick }>
							{ lng('FORGET_PASSWORD') }
					</var>
				</div>
			</div>
		</div>
  );
};

export default Login;
